mapper.select:
SELECT id , url , embed_url , broadcaster_id , broadcaster_name , creator_id , creator_name , video_id , game_id , language , title , view_count , created_at , thumbnail_url , duration FROM clip_twitch
AND broadcaster_id = #{broadcaster_id} AND broadcaster_name = #{broadcaster_name}
ORDER BY created_at DESC LIMIT #{page} OFFSET #{index}

SELECT ct.id, ct.url, ct.embed_url, ct.broadcaster_id, ct.broadcaster_name, ct.creator_id, ct.creator_name, ct.video_id,
ct.game_id, ct.language, ct.title, ct.view_count, ct.created_at, ct.thumbnail_url, ct.duration, ut.profile_image_url profile_image_url,
(ct.view_count / ((dayofyear(now()) - dayofyear(ct.created_at) + ((year(now()) - year(ct.created_at)) * 365 )) * (dayofyear(now()) - dayofyear(ct.created_at) + ((year(now()) - year(ct.created_at)) * 365 )))  ) points
FROM managed_follow mf
LEFT JOIN user_twitch ut ON mf.to_user = ut.id
LEFT OUTER JOIN clip_twitch ct ON ct.broadcaster_id = ut.id
WHERE mf.user_id = #{user_id}
AND (ct.view_count /((dayofyear(now()) - dayofyear(ct.created_at) + ((year(now()) - year(ct.created_at)) * 365 )) * (dayofyear(now()) - dayofyear(ct.created_at) + ((year(now()) - year(ct.created_at)) * 365 )))  ) < #{points}
ORDER by points desc limit #{limit};

mapper.update:
UPDATE clip_twitch

    , url = #{url}
    , embed_url = #{embed_url}
    , broadcaster_id = #{broadcaster_id}
    , broadcaster_name = #{broadcaster_name}
    , creator_id = #{creator_id}
    , creator_name = #{creator_name}
    , video_id = #{video_id}
    , game_id = #{game_id}
    , language = #{language}
    , title = #{title}
    , view_count = #{view_count}
    , created_at = #{created_at}
    , thumbnail_url = #{thumbnail_url}
    , duration = #{duration}


    AND id = #{id}
