mapper.select:
SELECT user_id, to_user
FROM managed_follow
WHERE user_id = #{user_id} AND to_user = #{to_user}

SELECT user_id, to_user
FROM managed_follow
WHERE user_id = #{user_id}

SELECT user_id, to_user
FROM managed_follow
WHERE user_id = #{user_id}
ORDER BY to_user
LIMIT #{left_num}, #{right_num}
