mapper.select:
SELECT user_id, user_pwd, user_nickname, user_email, twitch_user_id, enable
FROM user
WHERE user_id = #{user_id}

SELECT user_id, user_pwd, user_nickname, user_email, twitch_user_id, enable
FROM user
WHERE twitch_user_id = #{twitch_user_id}

SELECT user_id, authority
FROM user_auth
WHERE user_id = #{user_id}

mapper.update:
UPDATE user
SET user_pwd = #{user_pwd}, user_email = #{user_email}, user_nickname = #{user_nickname}, twitch_user_id = #{twitch_user_id}, enable = #{enable}
WHERE user_id = #{user_id}
